func TestValidParenthese(t *testing.T) {

	for _, ts := range tests {
		testValidParenthese := fmt.Sprintf("%v %v",ts.s, ts.want)
		t.Run(testValidParenthese, func(t *testing.T) {
			got := isValid(ts.s)
			if got != ts.want {
				t.Errorf("got %v, wanted %v", got, ts.want)
			}
		})
	}
}

func Test_Valid_Parenthese(t *testing.T) {
	for _, fn := range funcs {
		for _, ts := range tests {
			test := ts
			testValidParenthese := fmt.Sprintf("%v %v",ts.s,ts.want)
			t.Run(testValidParenthese, func(t *testing.T) {
				t.Parallel()
				if have := fn(test.s); have != test.want {
					t.Errorf(`
					wanted %+v
					have %+v`, 
					test.want, have)
				}
			})
		}
	}
}
